//Obtendo a respota da aquisição da API
import React, { useEffect, useState } from "react";
import api from "../../services/api";
import "./index.css";

const Comics = () => {
  const [comics, setComics] = useState([]);

  useEffect(() => {
    api
      .get('/comics')
      .then(response => {setComics(response.data.data.results);
      })
      .catch(err => console.log(err))
    }, []);
      
    return(
      //Inicio da div container
     <div className="container">  

      <h1>Quadrinhos</h1>
      <div className="quadrinhos">
      <img classeName="fundo"
      src="https://images6.alphacoders.com/755/thumb-1920-755336.jpg"  alt="fundo" 
        />
      
      <ul>
        {comics.map(comics =>{
          return (
              <li key={comics.id}>
                <img width='100' src = {`${comics.thumbnail.path}.${comics.thumbnail.extension}`} alt={`Foto do + ${comics.title}`} />
                <span className="name">{comics.title}</span>
              </li>
            )
          })}
        </ul>
        </div>


        <div className="Menu-lateral">
          <ul>
            <li > <a href=""></a>Quadrinhos </li>
            <li > <a href=""></a>Comprar</li>
            <li > <a href=""></a>Alugar</li>
            <li > <a href=""></a>Coleçao</li>
          </ul>
        </div>












     </div> //Fim da div container
    )
};

export default Comics;
